/* Example tweaks for STANDARD layout only (not for custom HTML) */

/* Align integers left */
[data-fieldtype="Int"] .value { text-align: left; }

/* Softer section separators */
.section-break {
  padding: 18px 0;
  border-bottom: 1px solid #eaecef;
}
.section-break:last-child {
  padding-bottom: 0;
  border-bottom: 0;
}

/* Make label/value spacing tighter for print */
.form-column .control-label { margin-bottom: 2px; color: #5b6672; }
.form-column .control-input-wrapper { margin-bottom: 6px; }

/* Hide truly empty values to reduce clutter */
.value:empty { display: none; }
